    private void ReaumurActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_ReaumurActionPerformed
        Double r = Double.parseDouble(Celcius.getText());
        double v = reaumur(r);
        String h = new Double(v).toString();
        Hasil.setText(h + " 째R");
    }//GEN-LAST:event_ReaumurActionPerformed

