    private void FahrenheitActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_FahrenheitActionPerformed
        Double f = Double.parseDouble(Celcius.getText());
        double v = fahrenheit(f);
        String h = new Double(v).toString();
        Hasil.setText(h + " 째F");
    }//GEN-LAST:event_FahrenheitActionPerformed

    private void KelvinActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_KelvinActionPerformed
        Double k = Double.parseDouble(Celcius.getText());
        double v = kelvin(k);
        String h = new Double(v).toString();
        Hasil.setText(h + " 째K");
    }//GEN-LAST:event_KelvinActionPerformed

