    private double fahrenheit(double n) {
        double f = (n * 9 / 5) + 32;
        return f;
    }

    private double kelvin(double n) {
        double k = n + 273.15;
        return k;
    }

    private double reaumur(double n) {
        double r = n * 4 / 5;
        return r;
    }

