    private void jButton1MouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jButton1MouseClicked
        output.setLineWrap(true);
        if (segitiga.isSelected()) {
            if (alas.getText().equals("") || tinggi.getText().equals("")) {
                output.setText("isi kolom alas dan tinggi terlebih dahulu");
            } else {
                Double x = Double.parseDouble(alas.getText());
                Double y = Double.parseDouble(tinggi.getText());
                Double n = segitiga(x, y);
                String s = new Double(n).toString();
                output.setText("Luas segitiga adalah " + s);
            }
        } else if (trapesium.isSelected()) {
            if (nilaiA.getText().equals("") || nilaiB.getText().equals("") || tinggi.getText().equals("")) {
                output.setText("isi kolom a, b, dan tinggi terlebih dahulu");
            } else {
                Double a = Double.parseDouble(nilaiA.getText());
                Double b = Double.parseDouble(nilaiB.getText());
                Double t = Double.parseDouble(tinggi.getText());
                Double v = trapesium(a, b, t);
                String f = new Double(v).toString();
                output.setText("Luas trapesium adalah " + f);
                
            }
        }
    }//GEN-LAST:event_jButton1MouseClicked

