    /**
     * @param args the command line arguments
     */
    public Double segitiga(Double alas, Double tinggi) {
        Double luas = (alas * tinggi) / 2;
        return luas;
    }

    public Double trapesium(Double a, Double b, Double tinggi) {
        Double luas = ((a + b) / 2) * tinggi;
        return luas;
    }

